mapper.select:
SELECT


,address.yonghu_id as addressYonghuId
,address.address_name as addressName
,address.address_phone as addressPhone
,address.address_dizhi as addressDizhi
,address.isdefault_types as isdefaultTypes
,shangpin.shangjia_id as shangpinShangjiaId
,shangpin.shangpin_name as shangpinName
,shangpin.shangpin_uuid_number as shangpinUuidNumber
,shangpin.shangpin_photo as shangpinPhoto
,shangpin.shangpin_types as shangpinTypes
,shangpin.shangpin_kucun_number as shangpinKucunNumber
,shangpin.shangpin_old_money as shangpinOldMoney
,shangpin.shangpin_new_money as shangpinNewMoney
,shangpin.shangpin_clicknum as shangpinClicknum
,shangpin.shangpin_content as shangpinContent
,shangpin.shangxia_types as shangxiaTypes
,shangpin.shangpin_delete as shangpinDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney

FROM shangpin_order  a
left JOIN address address ON a.address_id = address.id
left JOIN shangpin shangpin ON a.shangpin_id = shangpin.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.shangpin_order_uuid_number like CONCAT('%',#{params.shangpinOrderUuidNumber},'%')


        and (
            a.address_id = #{params.addressId}
        )


        and (
            a.shangpin_id = #{params.shangpinId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and a.buy_number >= #{params.buyNumberStart}   


          and a.buy_number <= #{params.buyNumberEnd}   


        and a.buy_number = #{params.buyNumber}


          and a.shangpin_order_true_price >= #{params.shangpinOrderTruePriceStart}   


          and a.shangpin_order_true_price <= #{params.shangpinOrderTruePriceEnd}   


        and a.shangpin_order_courier_name like CONCAT('%',#{params.shangpinOrderCourierName},'%')


        and a.shangpin_order_courier_number like CONCAT('%',#{params.shangpinOrderCourierNumber},'%')


        and a.shangpin_order_types = #{params.shangpinOrderTypes}


        and a.shangpin_order_payment_types = #{params.shangpinOrderPaymentTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.address_id IS NOT NULL


        and address.address_name like CONCAT('%',#{params.addressName},'%')


        and address.address_phone like CONCAT('%',#{params.addressPhone},'%')


        and address.address_dizhi like CONCAT('%',#{params.addressDizhi},'%')


        and address.isdefault_types = #{params.isdefaultTypes}



          and UNIX_TIMESTAMP(address.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(address.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


          and UNIX_TIMESTAMP(address.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(address.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 



        and a.shangpin_id IS NOT NULL


        and shangpin.shangjia_id = #{params.shangjiaId}


        and shangpin.shangpin_name like CONCAT('%',#{params.shangpinName},'%')


        and shangpin.shangpin_uuid_number like CONCAT('%',#{params.shangpinUuidNumber},'%')


        and shangpin.shangpin_types = #{params.shangpinTypes}



          and shangpin.shangpin_kucun_number >= #{params.shangpinKucunNumberStart}   


          and shangpin.shangpin_kucun_number <= #{params.shangpinKucunNumberEnd}   


        and shangpin.shangpin_kucun_number = #{params.shangpinKucunNumber}


          and shangpin.shangpin_old_money >= #{params.shangpinOldMoneyStart}   


          and shangpin.shangpin_old_money <= #{params.shangpinOldMoneyEnd}   


          and shangpin.shangpin_new_money >= #{params.shangpinNewMoneyStart}   


          and shangpin.shangpin_new_money <= #{params.shangpinNewMoneyEnd}   


          and shangpin.shangpin_clicknum >= #{params.shangpinClicknumStart}   


          and shangpin.shangpin_clicknum <= #{params.shangpinClicknumEnd}   


        and shangpin.shangpin_clicknum = #{params.shangpinClicknum}


        and shangpin.shangpin_content like CONCAT('%',#{params.shangpinContent},'%')


        and shangpin.shangxia_types = #{params.shangxiaTypes}



          and shangpin.shangpin_delete >= #{params.shangpinDeleteStart}   


          and shangpin.shangpin_delete <= #{params.shangpinDeleteEnd}   


        and shangpin.shangpin_delete = #{params.shangpinDelete}


          and UNIX_TIMESTAMP(shangpin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(shangpin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


order by a.${params.sort} ${params.order}
